const allLocations = [...QUICK_LOCATIONS, ...locations];

export const getLocations = (): {
  locations: Location[];
  totalRecords: number;
  quickLocations: Location[];
} => {
  return {
    // locations: locations.slice(offset, offset + limit),
    quickLocations: QUICK_LOCATIONS,
    locations: locations.sort((a, b) =>
      a.countryName.localeCompare(b.countryName),
    ),
    totalRecords: locations.length,
  };
};

export const fetchUserLocationInternal = async (): Promise<Location> => {
  const services = [
    { url: "https://ipapi.co/json/", name: "ipapi" },
    // { url: 'https://ip-api.com/json/', name: 'ip-api' }, // no free tire
    { url: "https://api.ipbase.com/v1/json/", name: "ipbase" },
  ];

  let location = {
    countryName: "World",
    countryCode: "world",
  };

  for (const service of services) {
    try {
      const response = await fetch(service.url);
      const data = await response.json();

      if (data && !data.error && data.country_name) {
        location = {
          countryName: data.country_name || data.country || "World",
          countryCode: data.country_code || data.countryCode || "world",
        };
        break; // Exit loop on first successful response
      }
    } catch (error: any) {
      console.warn(`${service.name} failed:`, error.message);
      continue;
    }
  }

  const matchedLocation = allLocations.find(
    (loc) =>
      loc.countryName.toLowerCase() === location.countryName.toLowerCase() ||
      loc.countryCode.toLowerCase() === location.countryCode.toLowerCase(),
  );

  return matchedLocation!;
};

export const fetchUserLocation = async (): Promise<Location> => {
  if (
    typeof chrome !== "undefined" &&
    chrome.runtime &&
    !chrome.runtime.sendMessage
  ) {
    // We are in the background script
    return fetchUserLocationInternal();
  }

  try {
    return await sendToBackground<Location>({
      type: MESSAGE_TYPES.FETCH_LOCATION,
    });
  } catch (error) {
    console.error("AI Watch: Failed to fetch location via background", error);
    return fetchUserLocationInternal(); // Fallback to local fetch if bridge fails
  }
};
